<template>
    <div :class="classObject">
        <a
                @click.once.prevent="initAudioElements"
                @touchstart="$emit('click')"
                href="#">
            <span class="word">{{e(labels.click)}}</span><span v-if="false" class="emoji">üîà</span>
            <!--<span v-else-if="inProgress" class="emoji progress">üèÉüèª‚ÄçÔ∏èÔ∏è</span>-->
            <span v-else class="emoji">üîä</span>
            <!--<span>{{browserCanAutoplay}}</span>-->

        </a>
    </div>
</template>

<script>
    import {labels} from '../assets/content'
    import state from '../state'
    import e from '../localizedContent'
    export default {
        name: "AudioActivation",
        data(){
            return {
                labels,
                state,
                audioTestElement: null,
                audioGivingElements: null,
                initializedElements: 0,
                inProgress: false,
            }
        },
        created(){
            this.checkBrowserAutoplay()
        },
        mounted(){
        },
        methods: {
            e,
            initAudioElements(){
                this.inProgress = true
                this.audioGivingElements = Array.prototype.slice.call(document.querySelectorAll('audio, video'))

                let tmpArray = this.audioGivingElements ? this.audioGivingElements.slice() : []
                tmpArray.push(this.audioTestElement)

                for(let i = 0; i < tmpArray.length; i++){
                    this.initAudioElement(tmpArray[i])
                }

            },
            initAudioElement(audioElement){
                audioElement.play().then(() => {
                    audioElement.pause()
                    this.initializedElements++
                })

            },
            checkBrowserAutoplay(){

                console.log("check")

                const mp3 = 'data:audio/mpeg;base64,/+MYxAAAAANIAUAAAASEEB/jwOFM/0MM/90b/+RhST//w4NFwOjf///PZu////9lns5GFDv//l9GlUIEEIAAAgIg8Ir/JGq3/+MYxDsLIj5QMYcoAP0dv9HIjUcH//yYSg+CIbkGP//8w0bLVjUP///3Z0x5QCAv/yLjwtGKTEFNRTMuOTeqqqqqqqqqqqqq/+MYxEkNmdJkUYc4AKqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqq'
                const ogg = 'data:audio/ogg;base64,'

                try{

                    if(this.audioTestElement === null) this.audioTestElement =  new Audio();
                    this.audioTestElement.src = null
                    let src = this.audioTestElement.canPlayType('audio/ogg') ? ogg : mp3
                    this.audioTestElement.autoplay = true

                    this.audioTestElement.addEventListener('play', () => {
                        this.state.setCanAutoplayAudio(true)
                    })

                    this.audioTestElement.src = src
                }catch(e){
                    console.log("ERROR NO AUTO PLAY")
                }

            }
        },
        computed: {
            noActivationNeeded(){
                return !this.state.getMediaElementsLoaded() || this.state.getCanAutoplayAudio()
            },
            classObject(){
                return {
                    'd-flex': true,
                    'justify-content-center': true,
                    'audio-activation': true,
                    'hide': this.noActivationNeeded
                }
            },
            mediaElementsWithTest(){
                let tmpArray = this.audioGivingElements ? this.audioGivingElements.slice() : []
                tmpArray.push(this.audioTestElement)
                return tmpArray
            }
        },
        watch: {
            initializedElements(to, from){
                if(to >= this.mediaElementsWithTest.length){
                    this.checkBrowserAutoplay()
                }
            }
        }
    }
</script>

<style lang="scss" scoped>

    .hide{
        opacity: 0;
        pointer-events: none;
    }

    .audio-activation{

        transition: opacity 300ms ease-out;

        margin-top: 2em;
        margin-bottom: 2em;
        font-size: 4em;

        .word{
            text-decoration: underline;
        }

        .emoji.progress{
            display: inline-block;
            -moz-transform: scale(-1, 1);
            -webkit-transform: scale(-1, 1);
            -o-transform: scale(-1, 1);
            -ms-transform: scale(-1, 1);
            transform: scale(-1, 1);
        }

        a{
            text-decoration: none;
        }

        a:hover{
            text-decoration: none;

            .word{
                text-decoration: underline;
            }
        }
    }
</style>